package mc.CushyPro.PluginName;import java.lang.reflect.Field;import java.util.UUID;import org.bukkit.Location;import org.bukkit.Material;import org.bukkit.block.Block;import org.bukkit.craftbukkit.v1_12_R1.CraftWorld;import org.bukkit.inventory.ItemStack;import org.bukkit.inventory.meta.ItemMeta;import org.bukkit.inventory.meta.SkullMeta;import com.mojang.authlib.GameProfile;import com.mojang.authlib.properties.Property;import net.minecraft.server.v1_12_R1.BlockPosition;import net.minecraft.server.v1_12_R1.TileEntitySkull;public class Hat {	public static ItemStack getSkull(String url) {		ItemStack head = new ItemStack(Material.SKULL_ITEM, 3);		if (url.isEmpty())			return head;		SkullMeta headMeta = (SkullMeta) head.getItemMeta();		GameProfile profile = new GameProfile(UUID.randomUUID(), null);		profile.getProperties().put("textures", new Property("textures", url));		Field profileField = null;		try {			profileField = headMeta.getClass().getDeclaredField("profile");			profileField.setAccessible(true);			profileField.set(headMeta, profile);		} catch (Exception e1) {			e1.printStackTrace();		}		head.setItemMeta((ItemMeta) headMeta);		return head;	}	public static void SetBlockSkull(Location loc, String url) {		Block b = loc.getBlock();		b.setType(Material.SKULL_ITEM);		GameProfile profile = new GameProfile(UUID.randomUUID(), null);		profile.getProperties().put("textures", new Property("textures", url));		TileEntitySkull skullTile = (TileEntitySkull) ((CraftWorld) b.getWorld()).getHandle()				.getTileEntity(new BlockPosition(b.getX(), b.getY(), b.getZ()));		skullTile.setGameProfile(profile);		b.getState().update(true);	}}